mapper.select:
select ca.cart_num , ca.cart_cnt as order_cnt , be.beans_price * ca.cart_cnt * (ca.cart_weight/100) as order_price , ca.beans_num , be.beans_name , be.beans_price , be.beans_taste , be.beans_img , ca.cart_weight , ca.cart_grind , ca.member_num from coffee_cart ca join coffee_beans be on ca.beans_num = be.beans_num where ca.cart_num in
${item}
and be.beans_count not in(0) order by ca.cart_num desc

mapper.update:
update final_member
   set member_point = member_point + #{savePoint} - #{usePoint}
 where member_num = #{memberNum}
